import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0" +
  " hover-elevate active-elevate-2",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground border border-primary-border",
        destructive:
          "bg-destructive text-destructive-foreground border border-destructive-border",
        outline:
          // Shows the background color of whatever card / sidebar / accent background it is inside of.
          // Inherits the current text color.
          " border [border-color:var(--button-outline)]  shadow-xs active:shadow-none ",
        secondary: "border bg-secondary text-secondary-foreground border border-secondary-border ",
        // Add a transparent border so that when someone toggles a border on later, it doesn't shift layout/size.
        ghost: "border border-transparent",
      },
      // Heights are set as "min" heights, because sometimes Ai will place large amount of content
      // inside buttons. With a min-height they will look appropriate with small amounts of content,
      // but will expand to fit large amounts of content.
      size: {
        default: "min-h-9 px-4 py-2",
        sm: "min-h-8 rounded-md px-3 text-xs",
        lg: "min-h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  },
)
Button.displayName = "Button"

export { Button, buttonVariants }

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import { nanoid } from "nanoid";
import { fileURLToPath } from "url";
import { dirname } from "path";

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    configFile: 'vite.config.ts',
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        __dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  // In production, __dirname is dist/server/, but static files are in dist/public/
  // So we need to go up one level (..) to reach dist/, then into public/
  const distPath = path.resolve(__dirname, "..", "public");
  // Read from APP_BASE_PATH, default to empty string (root deployment)
  // Normalize: ensure it starts with / if non-empty, and doesn't end with /
  let basePath = process.env.APP_BASE_PATH || '';
  if (basePath && !basePath.startsWith('/')) {
    basePath = '/' + basePath;
  }
  basePath = basePath.replace(/\/$/, '');

  console.log(`[SERVER DEBUG] APP_BASE_PATH: "${basePath}" | distPath: "${distPath}"`);

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  // DEBUG: Log all requests to the base path
  app.use((req, res, next) => {
    if (!basePath || req.path.startsWith(basePath)) {
      console.log(`[STATIC DEBUG] ${req.method} ${req.path}`);
    }
    next();
  });

  // Serve static files - handle both root and subpath deployments
  if (basePath) {
    // Subpath deployment: serve at /inboxai/*
    app.use(basePath, express.static(distPath));

    // Fall through to index.html if the file doesn't exist
    app.use(`${basePath}/*`, (_req, res) => {
      console.log(`[ROUTER FALLBACK] Serving index.html for ${_req.path}`);
      res.sendFile(path.resolve(distPath, "index.html"));
    });
  } else {
    // Root deployment: serve at /*
    app.use(express.static(distPath));

    // Fall through to index.html if the file doesn't exist
    app.use("/*", (_req, res) => {
      console.log(`[ROUTER FALLBACK] Serving index.html for ${_req.path}`);
      res.sendFile(path.resolve(distPath, "index.html"));
    });
  }

  // Redirect root to the app base path (only when deploying to a subpath)
  if (basePath) {
    app.get('/', (_req, res) => {
      console.log(`[SERVER DEBUG] Root redirect: / -> ${basePath}`);
      res.redirect(basePath);
    });
  }
}

