  async getUpcomingEvents(limit: number = 10): Promise<CalendarEvent[]> {
    const now = new Date();
    const result = await this.db.select().from(calendarEvents)
      .where(gte(calendarEvents.startTime, now))
      .orderBy(asc(calendarEvents.startTime))
      .limit(limit);
    return result;
