    const urgentItems: DashboardData['urgentItems'] = [];

    urgentEmails.slice(0, 3).forEach((email) => {
      urgentItems.push({
        id: email.id,
        type: 'email',
        title: email.subject,
        description: email.snippet || '',
        priority: 'high',
        from: email.from,
        quickActions: [
